--listing changes
SELECT 
  'Animalia' as Kingdom, S.SpcRecID, L.LegListing, L.LegDateListed, C.CtyRecID, L.LegNotes
FROM ORWELL.animals.dbo.species AS S
  INNER JOIN ORWELL.animals.dbo.genus AS G ON S.SpcGenRecID = G.GenRecID 
  INNER JOIN ORWELL.animals.dbo.family AS F ON G.GenFamRecID = F.FamRecID 
  INNER JOIN ORWELL.animals.dbo.taxorder AS O ON F.FamOrdRecID = O.OrdRecID 
  INNER JOIN ORWELL.animals.dbo.taxclass AS CL ON O.OrdClaRecID = CL.ClaRecID
  LEFT JOIN ORWELL.animals.dbo.legal AS L ON S.SpcRecID = L.LegSpcRecID
  LEFT JOIN ORWELL.animals.dbo.legalname AS LN ON L.LegLnmRecID = LN.LnmRecID
  LEFT JOIN ORWELL.animals.dbo.Country as C ON L.LegISO2 = C.CtyISO2
WHERE S.SpcStatus = 'A' AND ClaName = 'MAMMALIA' AND LN.LnmRecID = 3
ORDER BY S.SpcRecID;

